require 'spec_helper'
require_relative '../lib/letter_frequency'

describe 'Letter Frequency' do
  [['aabbcc', 'YES'],
   ['baacdd', 'NO'],
   ['aabbccc', 'YES'],
   ['aabbc', 'YES'],
   ['abcccc', 'NO'],
   ['aabbcd', 'NO'],
   ['aaccc', 'YES'],
   ['', 'YES']].each do |word, expected|
    it "given the word #{word} it returns #{expected}" do
      expect(isValid(word)).to eq(expected)
    end
  end
end
